import React from 'react';
import { useState, useEffect } from 'react';
import {connect} from 'react-redux'
import * as actionCreators from '../store/actions/actionCreators'

function Login(props) {

  function openTab(tabName) {
    var i;
    var x = document.getElementsByClassName('Tab');
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";
    }
    document.getElementById(tabName).style.display = "block";
  }

  const [user, setUser] = useState({})

  const handleChange = (e) => {
    setUser({
      [e.target.name]: e.target.value
    })
  }

  return (
    <div id='Login' className='homeTab' style={{display: 'none'}}>
      <input type='text' name='username' onChange={handleChange}></input>
      <input type='password' name='password' onChange={handleChange}></input>
      <button onClick={() => props.logUserIn(user)}>Login</button>
      <button onClick={openTab("Register")}>Register</button>
      <button onClick={openTab("PlayAsGuest")}>Play As Guest</button>
    </div>
  )
}

const mapDispatchToProps = (dispatch) => {
  return {
    logUserIn: (user) => dispatch(actionCreators.logUserIn(user))
  }
}

export default connect(null,mapDispatchToProps)(Login)
