import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.ctr = 0
  }

  handleIncrement() {
    props.prop.ctr += 1
    console.log(this.ctr)
  }

  render() {
    return (
      <div>
      <h1>Count will be displayed here</h1>
      <button onClick={this.handleIncrement}>Increment</button>
      </div>
    )
  }
}

export default Counter
