import React, {Component} from 'react';
import {AutoClick} from './Upgrades'

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      increaseAmount: 1
    }
  }

  increaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr + this.state.increaseAmount
    })
  }

  autoIncrement = () => {
    setInterval(this.increaseIncrement, this.state.inteval)
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      <AutoClick increment={this.autoIncrement}/>
      </div>
    )
  }
}

export default Counter
