import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      doubleClick: {
        buttonDisabled: true,
        effectEnabled: false
      }
    }
  }

  doubleClick = () => {
    this.setState({
      doubleClick: {
        buttonDisabled: false,
        effectEnabled: true
      }
    })
  }

  increaseIncrement = () => {
    if(this.state.doubleClick.effectEnabled == true) {
      this.setState({
        ctr: this.state.ctr + 2
      })
    } else {
      this.setState({
        ctr: this.state.ctr + 1
      })
    }
    if(this.state.ctr == 100) {
      this.setState({
        doubleClick: {
          buttonDisabled: false
        }
      })
    }
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      <button className='Button' onClick={this.doubleClick} disabled={this.state.doubleClick.buttonDisabled}>Double Click</button>
      </div>
    )
  }
}

export default Counter
