import React, {Component} from 'react';
import {AutoClick} from './Upgrades'

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0
    }
  }

  increaseIncrementOne = () => {
    this.setState({
      ctr: this.state.ctr + 1
    })
  }

  increaseIncrement = () => {
    this.increaseIncrementOne()
  }

  autoPoke = () => {
    this.setState({
      autoClick: {
        tier: this.state.tier + 1,
        cost: this.state.tier + this.x,
        buttonDisabled: true,
        effectEnabled: true
      }
    })
    console.log(this.state)
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      <AutoClick />
      </div>
    )
  }
}

export default Counter
