import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0
    }
  }

  increaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr + 1
    })
  }

  decreaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr - 1
    })
  }

  render() {
    return (
      <div className='Counter'>
      <button className='Button' onClick={this.decreaseIncrement}>-</button>
      <p className='Value'>{this.state.ctr}</p>
      <button className='Button' onClick={this.increaseIncrement}>+</button>
      </div>
    )
  }
}

export default Counter
