import React, {Component} from 'react';

export class AutoClick extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tier: 1,
      cost: 10,
      buttonDisabled: true,
      effectEnabled: false,
      interval: 10000
      }
  }

  autoPoke = () => {

    let x = (this.state.cost/1.25) - (this.state.cost/2)

    this.setState({
      tier: this.state.tier + 1,
      cost: this.state.cost + Math.round(x),
      buttonDisabled: true,
      effectEnabled: true,
      interval: this.state.interval - this.state.interval*0.1
    },() => {
      let i = setInterval(this.props.increment(), this.state.interval)
    })
    console.log(this.state)

  }

  render() {
    return (
      <button onClick={this.autoPoke} >Auto Poke: Tier: {this.state.tier}, Cost: {this.state.cost}</button>
    )
  }
}
