import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      doubleClick: false
    }
  }

  increaseIncrement = () => {
    if(this.state.ctr = 100) {
      if(this.state.doubleClick = true) {
        this.setState({
          ctr: this.state.ctr + 2
        })
      } else {
        this.setState({
          ctr: this.state.ctr + 1,
        })
      }
    } else {
      this.setState({
        ctr: this.state.ctr + 1,
      })
    }
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      </div>
    )
  }
}

export default Counter
