import React from 'react';
import { useState, useEffect } from 'react';
import {connect} from 'react-redux'
import * as actionCreators from '../store/actions/actionCreators'

function Register(props) {

  function openTab(tabName) {
    var i;
    var x = document.getElementsByClassName('Tab');
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";
    }
    document.getElementById(tabName).style.display = "block";
  }

  const [newUser, setNewUser] = useState({})

  const handleChange = (e) => {
    setNewUser({
      [e.target.name]: e.target.value
    })
  }

  return (
    <div id='Register' className='homeTab'>
      <input type='text' name='email' onChange={handleChange}></input>
      <input type='text' name='username' onChange={handleChange}></input>
      <input type='password' name='password' onChange={handleChange}></input>
      <input type='password' name='confirmPassword' onChange={handleChange}></input>
      <button onClick={(newUser) => props.addNewUser(newUser)}>Register</button>
      <button onClick={openTab("Login")}>Login</button>
      <button onClick={openTab("PlayAsGuest")}>Play As Guest</button>
    </div>
  )
}

const mapDispatchToProps = (dispatch) => {
  return {
    addNewUser: (newUser) => dispatch(actionCreators.addNewUser(newUser))
  }
}

export default connect(null,mapDispatchToProps)(Register)
