import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      doubleClick: false
    }
  }

  doubleClick = () => {
    this.setState({
      doubleClick: true
    })
    console.log(this)
  }

  increaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr + 1
    })
    if(this.state.doubleClick = true) {
      this.setState({
        ctr: this.state.ctr + 2
      })
    }
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      <button className='Button' onClick={this.doubleClick}>Double Click</button>
      </div>
    )
  }
}

export default Counter
