    }
    setInterval(this.increaseIncrement, this.state.interval)
  }

  handleInterval = (counter) => {

    this.setState({
      interval: counter
    })

  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      <AutoClick increment={this.autoIncrement} interval={this.handleInterval} />
      </div>
    )
  }
}

export default Counter
