import React, {Component} from 'react';

export class AutoClick extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tier: 1,
      cost: 10,
      buttonDisabled: true,
      effectEnabled: false
      }
  }

  autoPoke = () => {

    let x = [0,1]
    for(let i = 0; i <= this.state.tier; i++){
      let c = x[i] + x[i + 1]
      x.push(c)
    }

    this.setState({
      tier: this.state.tier + 1,
      cost: this.state.cost + x[x.length - 1],
      buttonDisabled: true,
      effectEnabled: true
    })
  }

  render() {
    return (
      <button onClick={this.autoPoke} >Auto Poke: Tier: {this.state.tier}, Cost: {this.state.cost}</button>
    )
  }
}
