import React, {Component} from 'react';

export class AutoClick extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tier: 1,
      cost: 10,
      buttonDisabled: true,
      effectEnabled: false
      }
  }

  x = () => {
    let y = this.state.tier,
        a = 1,
        b = 1

    for(let i = 0; i <= y; i++) {
      let c = a + b,
          a = b,
          b = c
      return c
    }
  }

  autoPoke = () => {
    this.setState({
      tier: this.state.tier + 1,
      cost: this.state.tier + this.x.c,
      buttonDisabled: true,
      effectEnabled: true
    })
    console.log(this.state)
  }

  render() {
    return (
      <button onClick={this.autoPoke} >Auto Poke {this.state.tier}</button>
    )
  }
}
