import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      increase: '',
      decrease: ''
    }
  }

  increaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr + 1,
    })
  }

  decreaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr - 1,
      increase: false,
      decrease: true
    })
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Likes</p>
      <button className='Button' onClick={this.increaseIncrement} disabled={this.state.increase}>+</button>
      <button className='Button' onClick={this.decreaseIncrement} disabled={this.state.decrease}>-</button>
      </div>
    )
  }
}

export default Counter
