import * as actionTypes from './actionTypes'
import axios from 'axios'

export const addNewUserActionCreator = () => {
  return {
    type: actionTypes.ADD_NEW_USER
  }
}

export const addNewUser = (newUser) => {
  return (dispatch) => {
    axios.post('http://localhost:5000/user/addUser', newUser,{

    })
    .then(res => {
      dispatch(addNewUserActionCreator())
    })
  }
}

export const logUserInActionCreator = (response) => {
  return {
    type: actionTypes.LOG_USER_IN,
    response: response
  }
}

export const playAsGuest = () => {
  return {
    type: actionTypes.PLAY_AS_GUEST
  }
}

export const logUserIn = (user) => {
  return (dispatch) => {
    axios.get('http://localhost:5000/logUserIn', user)
    .then(res => {
      dispatch(logUserInActionCreator(res.data))
    })
  }
}
