import React from 'react';
import { useState, useEffect } from 'react';
import {connect} from 'react-redux'

function Header(props) {

  function openTab(tabName) {
    var i;
    var x = document.getElementsByClassName('homeTab');
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";
    }
    document.getElementById(tabName).style.display = "block";
  }

  function logout() {

  }

  return (
    <div id='Header' className="w3-bar w3-grey">
      <h1 className='w3-bar-item'>PiePoker</h1>
      { props.isAuthenticated ? null : <button className="w3-bar-item w3-button"><h2>Your Games</h2></button>}
      { props.isAuthenticated ? <button className="w3-bar-item w3-button" onClick={() => openTab("Register")}><h2>Register</h2></button> : null}
      { props.isAuthenticated ? <button className="w3-bar-item w3-button" onClick={() => openTab("Login")}><h2>Login</h2></button> : null}
      { props.isAuthenticated ? <button className="w3-bar-item w3-button" onClick={() => openTab("PlayAsGuest")}><h2>Play As Guest</h2></button> : null}
      { props.isAuthenticated ? null : <button className="w3-bar-item w3-button" onClick={() => logout()}><h2>Log Out</h2></button>}
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    isAuthenticated: state.userR.isAuthenticated
  }
}

export default connect(mapStateToProps)(Header)
