import React from 'react';
import { useState, useEffect } from 'react';

function Header(props) {

  function openTab(tabName) {
    var i;
    var x = document.getElementsByClassName('homeTab');
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";
    }
    document.getElementById(tabName).style.display = "block";
  }

  function logout() {

  }

  return (
    <div className="w3-bar w3-white">
      <h1 className='w3-bar-item'>PiePoker</h1>
      { props.isAuthenticated ? null : <button className="w3-bar-item w3-button">Your Games</button>}
      { props.isAuthenticated ? <button className="w3-bar-item w3-button" onClick={() => openTab("Register")}>Register</button> : null}
      { props.isAuthenticated ? <button className="w3-bar-item w3-button" onClick={() => openTab("Login")}>Login</button> : null}
      { props.isAuthenticated ? <button className="w3-bar-item w3-button" onClick={() => openTab("PlayAsGuest")}>Play As Guest</button> : null}
      { props.isAuthenticated ? null : <button className="w3-bar-item w3-button" onClick={() => logout()}>Log Out</button>}
    </div>
  )
}

export default Header
