import React from 'react';
import Login from './components/Login';
import Register from './components/Register';
import { useState, useEffect } from 'react';
import './App.css';
import {connect} from 'react-redux'
import * as actionCreators from './store/actions/actionCreators'

function App(props) {

  function openTab(tabName) {
    var i;
    var x = document.getElementsByClassName('homeTab');
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";
    }
    document.getElementById(tabName).style.display = "block";
  }

  const [guest, setGuest] = useState({})

  const handleChange = (e) => {
    setGuest({
      [e.target.name]: e.target.value
    })
  }

  return (
    <div className='HomePage'>
      <Register />
      <Login />
      <div>
        <button onClick={() => openTab("Register")}>Register</button>
        <button onClick={() => openTab("Login")}>Login</button>
        <button onClick={() => openTab("PlayAsGuest")}>Play As Guest</button>
      </div>
      <div id='PlayAsGuest' className='homeTab' style={{display: 'none'}}>
        <input type='text' name='guestGame' onChange={handleChange}></input>
        <button onClick={() => props.playAsGuest()}>Play As Guest</button>
      </div>
    </div>
  )
}

const mapDispatchToProps = (dispatch) => {
  return {
    playAsGuest: () => dispatch(actionCreators.playAsGuest())
  }
}

export default connect(null,mapDispatchToProps)(App)
