import React from 'react';
import { useState, useEffect } from 'react';

function Header(props) {

  function openTab(tabName) {
    var i;
    var x = document.getElementsByClassName('homeTab');
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";
    }
    document.getElementById(tabName).style.display = "block";
  }

  return (
    <div className='Header'>
      <h1>PiePoker</h1>
      <h2>Your Games</h2>
      { props.isAuthenticated ? <button onClick={() => openTab("Register")}>Register</button> : null}
      { props.isAuthenticated ? <button onClick={() => openTab("Login")}>Login</button> : null}
      { props.isAuthenticated ? null : <button onClick={() => openTab("PlayAsGuest")}>Play As Guest</button>}
    </div>
  )
}

export default Header
