import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      increase: '',
      decrease: ''
    }
  }

  increaseIncrement = () => {
    this.setState({
      ctr: this.state.ctr + 1,
    })
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement} disabled={this.state.increase}><img src='Marshmallow.jpeg'/></button>
      </div>
    )
  }
}

export default Counter
