import React, {Component} from 'react';

class Counter extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctr: 0,
      doubleClick: {
        buttonDisabled: true,
        effectEnabled: false
      },
      squeeze: {
        buttonDisabled: true,
        effectEnabled: false
      },
      doubleAutoClick: {
        buttonDisabled: true,
        effectEnabled: false
      }
    }
  }

  increaseIncrementOne = () => {
    this.setState({
      ctr: this.state.ctr + 1
    })
  }

  increaseIncrement = () => {
    if(this.state.squeeze.effectEnabled == true) {
      this.increaseIncrementFive()
    } else if (this.state.doubleClick.effectEnabled == true) {
      this.increaseIncrementTwo()
    } else {
      this.increaseIncrementOne()
    }
    if(this.state.ctr == 10 && this.state.doubleClick.effectEnabled == false) {
      this.setState({
        doubleClick: {
          buttonDisabled: false
        }
      })
    }
    if(this.state.ctr == 10 && this.state.autoClick.effectEnabled == false) {
      this.setState({
        autoClick: {
          buttonDisabled: false
        }
      })
    }
    if(this.state.ctr == 10 && this.state.squeeze.effectEnabled == false) {
      this.setState({
        squeeze: {
          buttonDisabled: false
        }
      })
    }
    if(this.state.ctr == 10 && this.state.doubleAutoClick.effectEnabled == false) {
      this.setState({
        doubleAutoClick: {
          buttonDisabled: false
        }
      })
    }
  }

  render() {
    return (
      <div className='Counter'>
      <p className='Value'>{this.state.ctr} Pokes</p>
      <button className='Button' onClick={this.increaseIncrement}><img id='Marshmallow' src='marshmallow.jpeg'/></button>
      <button className='Button' onClick={this.doubleClick} disabled={this.state.doubleClick.buttonDisabled}>Double Poke</button>
      <button className='Button' onClick={this.autoClick} disabled={this.state.autoClick.buttonDisabled}>Auto Poke</button>
      <button className='Button' onClick={this.doubleAutoClick} disabled={this.state.doubleAutoClick.buttonDisabled}>Double Auto Poke</button>
      <button className='Button' onClick={this.squeeze} disabled={this.state.squeeze.buttonDisabled}>Squeeze</button>
      </div>
    )
  }
}

export default Counter
